def pico_tag(number)
    return PICO_STATUS.keys()[PICO_STATUS.values().index(number)]
end

def pico_num(tag)
	n = PICO_STATUS[tag]
    if n==nil
        n = PICO_STATUS["PICO_NOT_USED"]
	end
    return n
end

